import { useState, useEffect, createContext, useContext } from 'react';
import auth from "../firebase";
import {
  applyActionCode,
  createUserWithEmailAndPassword,
  signInWithEmailAndPassword,
  signOut,
  } from "firebase/auth";

export const AuthContext = createContext()

export function UserAuthContextProvider({ children }) {

    const [user, setUser] = useState(); // State for a specific user
    const [verifyData, setVerifyDataState] = useState({error: '', validCode: null, verifiedCode: false}); // State for a user's email verification

    function register(email, password) {
      return createUserWithEmailAndPassword(auth, email, password);
    }

    function verifyEmail(actionCode) {
      return applyActionCode(auth, actionCode);
    }

    function logIn(email, password) {
      return signInWithEmailAndPassword(auth, email, password);
    }

    function logOut() {
      return signOut(auth);
    }

    useEffect(() => {
      const unsubscribe = auth.onAuthStateChanged( (currentuser) => {
        setUser(currentuser);
        // if(currentuser && !currentuser.emailVerified) {
        //   sendEmailVerification(currentuser);
        // }
        // TODO - see if you want to keep the above?
      });

      return () => {
        unsubscribe();
      };
    }, []);

    return (
      <AuthContext.Provider
        value={{ user, register, logIn, logOut, verifyEmail}}
      >
        {children}
      </AuthContext.Provider>
    );
  }

  export function useUserAuth() {
    return useContext(AuthContext);
  }
