import React from 'react'
import Loading from '../../../components/loading/Loading';
import { useUserAuth } from '../../../context/AuthProvider';
import AuthResult from '../AuthResult';

const VerifyEmail = ({actionCode}) => {

    const { verifyEmail } = useUserAuth();
    var verified = undefined;

    async function checkCode(e) {

        try {
            await verifyEmail(actionCode);
            verified = true;
            console.log("VERIFIED:", verified)
        }
        catch (e) {
            verified = false;
            console.log("VERIFIED:", verified)
        }
    }

    checkCode();

return typeof verified === 'undefined' ? (
    <Loading/> ) :
    verified? ( <AuthResult action={'Email verification'} result={'successful.'} message={'Welcome to rocketstart!'}/> )
            : ( <AuthResult action={'Email verification'} result={'unsuccessful.'} message={'Your request to verify your email has expired or the link has already been used. Please try resending a code.'}/>
    )
}

export default VerifyEmail
