import { useState, useEffect, createContext, useContext } from 'react';
import auth from "../firebase";
import {
    createUserWithEmailAndPassword,
    signInWithEmailAndPassword,
    generateEmailVerificationLink,
    signOut,
  } from "firebase/auth";

export const AuthContext = createContext()

export function UserAuthContextProvider({ children }) {

    const [user, setUser] = useState();

    function register(email, password) {
      return createUserWithEmailAndPassword(auth, email, password);
    }

    function generateVerificationLink(email) { // If email doesn't work, try passing user.
      return generateEmailVerificationLink(auth, email);
    }

    function logIn(email, password) {
      return signInWithEmailAndPassword(auth, email, password);
    }

    function logOut() {
      return signOut(auth);
    }

    useEffect(() => {
      const unsubscribe = auth.onAuthStateChanged( (currentuser) => {
        setUser(currentuser);
        // if(currentuser && !currentuser.emailVerified) {
        //   sendEmailVerification(currentuser);
        // }
        // TODO - see if you want to keep the above?
      });

      return () => {
        unsubscribe();
      };
    }, []);

    return (
      <AuthContext.Provider
        value={{ user, register, generateVerificationLink, logIn, logOut}}
      >
        {children}
      </AuthContext.Provider>
    );
  }

  export function useUserAuth() {
    return useContext(AuthContext);
  }
