import React from 'react'
import { useState } from 'react';

const VerbExample = ({verb, count}) => {

    const [badExample, setBadExample] = useState("");
    const [goodExample, setGoodExample] = useState("");

    // Example phrases
    if(verb === "to be") {
        setBadExample("I want to be a part of your team.");
        setGoodExample("I'd love to join your team.");
    }
    else if(verb === "to have") {
        setBadExample("I'd love to have a conversation with you.");
        setGoodExample("I'd love to chat with you.");
    }
    else if(verb === "there is" || verb === "there are") {
        setBadExample("There are plenty of strengths that make me stand out.");
        setGoodExample("I stand out because of these strengths.");
    }
    else if(verb === "was") {
        setBadExample("I was in the position for two years.");
        setGoodExample("I held the position for two years.");
    }

  return (
    <div>
        <div class="good__example">
            <span class="material-icons explanation__cross">close</span>
            <p class="badExample">{badExample}</p>
        </div>
        <div class="bad__example">
            <span class="material-icons explanation__check">done</span>
            <p class="goodExample">{goodExample}</p>
        </div>
        <hr class="horizontal__rule"></hr>
        <div style={{paddingBottom: ".5rem"}}></div>
    </div>
  )
}

export default VerbExample
