import { useState, useEffect } from 'react';
import auth from "../firebase";;

const AuthProvider = () => {
    const [currentUser, setCurrentUser] = useState(null);

    useEffect(() => {
      const unSubscribe = auth.onAuthStateChanged(auth, (user) =>
        setCurrentUser(user)
      );
      return unSubscribe;
    }, []);

    return currentUser;
}

export default AuthProvider
