import React from 'react'
import { useState, useEffect, useRef } from 'react';
import Loading from '../../../components/loading/Loading';
// import { useUserAuth } from '../../../context/AuthProvider';
import AuthResult from '../AuthResult';
import auth from "../../../firebase";
import {applyActionCode} from "firebase/auth";

const VerifyEmail = ({actionCode}) => {

    const [verified, setVerified] = useState(undefined);
    const timelineLoaded = useRef(false); // So useEffect only runs once

    console.log('Enter verify email');
    console.log('Action code', actionCode);

    useEffect(() => {
        async function checkCode() {

            try {
                // if(timelineLoaded.current) {
                    await applyActionCode(auth, actionCode);
                    console.log('Firebase call');
                // }
                setVerified(true);
            }
            catch (e) {
                setVerified(false);
            }
        }
        checkCode();
        timelineLoaded.current = true;
    }, [actionCode]);

    return typeof verified === 'undefined' ? ( <Loading/> ) :
            verified? ( <AuthResult action={'Email verification'} result={'successful.'} message={'Welcome to rocketstart!'}/> )
                    : ( <AuthResult action={'Email verification'} result={'unsuccessful.'} message={'Your request to verify your email has expired or the link has already been used. Please try resending a code.'}/>)
}

export default VerifyEmail
