import React from 'react';
import { useAuth } from '../../contexts/AuthContext';
import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

import './signUp.css';

const SignUp = () => {

    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");

    const navigate = useNavigate();
    const { currentUser, register } = useAuth();
    const [loading, setLoading] = useState(false);

    // Don't allow users to access registration while they're logged in
    useEffect(() => {
        if (currentUser) {
          navigate("/");
        }
    }, [currentUser, navigate]);

    async function handleFormSubmit(e) {
        e.preventDefault();

        if (password !== confirmPassword) {
            return alert("Passwords do not match");
        }

        try {
            setLoading(true);
            await register(email, password);
            navigate("/message");
        } catch (e) {
            alert("Failed to register");
        }

        setLoading(false);
    }

  return (
    <div>
        Sign Up
        <input type="text" placeholder="username" onChange={(e) => setEmail(e.target.value)}></input>
        <input type="password" placeholder="password" onChange={(e) => setPassword(e.target.value)}></input>
        <input type="password" placeholder="confirm password" onChange={(e) => setConfirmPassword(e.target.value)}></input>
        <button onSubmit={handleFormSubmit} disabled={loading}>Login</button>
    </div>
  )
}

export default SignUp
