import React from 'react'
import { useState } from 'react'
import { useDispatch } from 'react-redux'
import './verification.css'

const Verification = ({userEmail}) => {

  const dispatch = useDispatch;

  // Final user email
  const [email, setEmail] = useState(userEmail);

  // User email state in FE
  const [typedEmail, setTypedEmail] = useState('');

  // Button text change
  const [buttonText, setButtonText] = useState('Edit Email Address');

  // Resend verification email
  async function resendVerification(e) {
    try {
      dispatch(resendVerification(email)); // TODO: implement routing + BE logic
    } catch (e) {
      console.log(e)
    }
  }

  // When user is typing, but hasn't changed the text
  function editingEmail(typed) {
    setTypedEmail(typed);
    if(typed === '') {
      setButtonText('Edit Email Address');
      // TODO: input display none
    }
    else {
      setButtonText('Cancel;');
    }
  }

  // When the user clicks confirm
  async function handleEmailEdit() {
    if(typedEmail === 'Edit Email Address') {
      setButtonText('Edit Email Address');
      // TODO: input display none
      // TODO: popup for email sent
    }
    else {
      setEmail(typedEmail);
    }
    // TODO: use context
  }

  return (
    <div className='verification__bg'>
        <div className='verification__modal'>
            <h2 className='verification__modal-header'>Verification email sent!</h2>

            <p className='verification__modal-text'>
                Please check your inbox and activate your account to get started. Email sent to:
            </p>

            <p><font className='verification__modal-email'> {userEmail}</font></p>

            <button type="submit" className='verification__button' onClick={() => {resendVerification()}}>
              Resend Verification Email
            </button>

            <form onSubmit={handleEmailEdit}>
                <input
                  className=''
                  required
                  placeholder="Email"
                  autoComplete='email'
                  name='email'
                  autoFocus
                  onChange={(e) => editingEmail(e.target.value)}
                />
                <button type="submit" className='verification__button'>
                  {buttonText}
                </button>
          </form>
        </div>
    </div>
  )
}

export default Verification
