import React from 'react'
import { useState } from 'react'
import { useDispatch } from 'react-redux'
import './verification.css'

const Verification = ({userEmail}) => {

  const dispatch = useDispatch;
  const [email, setEmail] = useState(userEmail);

  async function resendVerification(e) {
      try {
        // await register(userData.email, userData.password);
        // dispatch(createUser(userData));
        // navigate("/message");

        dispatch(resendVerification(email));
      } catch (e) {
        console.log(e)
      }
  }


  return (
    <div className='verification__bg'>
        <div className='verification__modal'>
            <h2 className='verification__modal-header'>Verification email sent!</h2>
            <p className='verification__modal-text'>
                Please check your inbox and activate your account to get started. Email sent to:
            </p>
            <p><font className='verification__modal-email'> {userEmail}</font></p>
            <button type="submit" className='verification__button' onClick={() => {resendVerification()}}>
              Resend Verification Email
            </button>
            <button type="submit" className='verification__button'>
              Edit Email Address
            </button>
        </div>
    </div>
  )
}

export default Verification
